import React, { Component } from 'react';
import voting from '../../Voting';
import web3 from '../../web3';
import Layout from '../../components/Layout';
import {Header,Segment, Button} from 'semantic-ui-react';
class candidateShow extends Component{
    state={
        errorMessage:"",
        disabled:false,
        loading:false
    }
    static async getInitialProps(props){
        const candidate=await voting.methods.candidates(props.query.id).call();
        return {candidate};
    }
    onClick=async(event)=>{
        try{
            const accounts=await web3.eth.getAccounts();

        }catch(err){
            this.setState({errorMessage:err.message})
        }
    };
    render(){
        return (
            <Layout>
                <div>
                    <Header as="h3" attached="top">{this.props.candidate.name.substr(1,this.props.candidate.name.length-2)}</Header>
                    <Segment attached inverted >
                        <div>
                            <Header as="h4" color="red">current Votes:</Header>
                            {this.props.candidate.votes}
                        </div>
                        <div style={{marginTop:"5px"}}>
                            <Header as="h4" color="red">party:</Header>
                            {this.props.candidate.party.substr(1,this.props.candidate.party.length-2)}
                        </div>
                        <div style={{marginTop:"5px"}}>
                            <Header as="h4" color="red">description:</Header>
                            {this.props.candidate.description.substr(1,this.props.candidate.description.length-2)}
                        </div>
                        <div>
                        <Button style={{marginLeft:"500px"}} color="green" onClick={this.onClick} disabled={this.state.disabled} loading={this.state.loading}>
                            Vote
                        </Button>
                        </div>
                    </Segment>

                </div>
            </Layout>
        );
    }
}

export default candidateShow;
